CREATE TABLE Facturacion (
    id_factura INT PRIMARY KEY,
    id_cliente INT,
    id_servicio INT,
    fecha DATE,
    monto DECIMAL(10,2),
    estado_pago VARCHAR(20),
    FOREIGN KEY (id_cliente) REFERENCES dbo.Cliente(id_cliente),
    FOREIGN KEY (id_servicio) REFERENCES dbo.Servicio(id_servicio)
);

